    MySql datos = new MySql();
    
    private void Agregar_BTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Agregar_BTNActionPerformed

        String sql = "INSERT INTO `cliente` (`idcliente`, `identidad`, `cliente`, `direccion`, `telefono`, `sueldo`, `idlugar`, `estado`, `edadCliente`)"
        +"VALUES("+idCliente_TXT.getText()+", '"+Identidad_TXT.getText()+"', '"+Cliente_TXT.getText()+"', '"+Direccion_TXT.getText()+"', '"+Telefono_TXT.getText()+"', "+Sueldo_TXT.getText()+", "+idLugar_TXT.getText()+", "+Edad_TXT.getText()+", "+Edad_TXT.getText()+")";

        datos.EjecutarConsultas(sql);
        datos.LlenarTabla("Select * from cliente" , jTable1 );
        Agregar_BTN.setEnabled(false);
        Editar_BTN.setEnabled(true);
        Eliminar_BTN.setEnabled(true);

    }//GEN-LAST:event_Agregar_BTNActionPerformed

    private void Eliminar_BTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Eliminar_BTNActionPerformed

        String id = idCliente_TXT.getText();

        String sql = "DELETE FROM cliente WHERE idcliente = '" + id + "'";

        datos.EjecutarConsultas(sql);
        datos.LlenarTabla("SELECT * FROM cliente", jTable1);

        Agregar_BTN.setEnabled(true);
        Editar_BTN.setEnabled(false);
        Eliminar_BTN.setEnabled(false);

    }//GEN-LAST:event_Eliminar_BTNActionPerformed

