    private void Editar_BTNActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Editar_BTNActionPerformed

        String sql = "UPDATE `cliente` SET " +
        "`idcliente`='" + idCliente_TXT.getText() + "', " +
        "`identidad`='" + Identidad_TXT.getText() + "', " +
        "`cliente`='" + Cliente_TXT.getText() + "', " +
        "`direccion`='" + Direccion_TXT.getText() + "', " +
        "`telefono`='" + Telefono_TXT.getText() + "', " +
        "`sueldo`='" + Sueldo_TXT.getText() + "', " +
        "`idlugar`='" + idLugar_TXT.getText() + "', " +
        "`edadCliente`='" + Edad_TXT.getText() + "' " +
        "WHERE `idcliente` = '" + idCliente_TXT.getText() + "'";

        datos.EjecutarConsultas(sql);
        datos.LlenarTabla("SELECT * FROM cliente", jTable1);

        Agregar_BTN.setEnabled(false);
        Editar_BTN.setEnabled(false);
        Eliminar_BTN.setEnabled(true);
    }//GEN-LAST:event_Editar_BTNActionPerformed

